function insertTranslation(value,index,arr ){
    let numChars=value.translation.length;
    var tmpWords =value.translation.split(' ');
    let numWords=value.translation.split(' ').length;
    let numTokens=0;
    let wordBasedToken=true;
    var tmpTextValue = value.translation;
    var cuePartTokenCount=0;
    var cuePartWordCount=0;
    var xlateTokenRatio
    let xlatetarget=msg.translate_model_id;
    sentenceAndCuePart[index].xlateText = tmpTextValue;
    if (numWords==numChars||numWords==1 || xlatetarget=="en-zh" ||"en-zh-TW"){
        numWords = numChars;
        wordBasedToken=false;
    } else {
        numWords = numWords;
    }
    sentenceAndCuePart[index].xlateNumTokens = numTokens; 
    sentenceAndCuePart[index].xlatenumWords = numWords; //tmpTextValue.split(' ').length;
    sentenceAndCuePart[index].xlateNumChars = numChars;
    sentenceAndCuePart[index].xlatelanguage = msg.translate_model_id;
    // calc token ratio. Source language tokens divided by translated number of tokens. 
   // if (sentenceAndCuePart[index].numWords<sentenceAndCuePart[index].xlatenumWords){
   //     xlateTokenRatio = sentenceAndCuePart[index].numWords/sentenceAndCuePart[index].xlatenumWords;
   // }else{
        //use numWords to calculate ratio. Not really correct except but the best we can do without NLU || dictionary,..
        xlateTokenRatio = sentenceAndCuePart[index].xlatenumWords / sentenceAndCuePart[index].numWords;
 //   }

    sentenceAndCuePart[index].xlateTokenRatio = xlateTokenRatio;
    var cueParts = sentenceAndCuePart[index].cueParts;
    if (cueParts.length>1){
        for (i=0;i< cueParts.length; i++){
            if (i == cueParts.length -1){
                // the rest of text goes here. May be more than calculated 
                cuePartwordCount = tmpWords.length;
            }else{
                cuePartwordCount =cueParts[i].wordCount*xlateTokenRatio;
            }    
                cueParts[i].xlatecuewordCount = cuePartwordCount;
                if (wordBasedToken){
                    // will need to seperate out for language support. Some languages don't use spaces between tokens
                    cueParts[i].xlatecuePartText = tmpWords.slice(0,cuePartwordCount).join(' '); // No for Hanj languages                
                }else{
                    //tmpWords = value.translation; // Character based rather than word based so get chars. Chinese for example
                    tmpWords = tmpTextValue;//03242002
                    cueParts[i].xlatecuePartText = tmpWords.substr(0,cuePartwordCount);
                }

            //03242002 tmpWords = tmpWords.slice(cuePartwordCount);
            tmpTextValue = tmpTextValue.slice(cuePartwordCount); //03242002

        }              
    }else{
        cueParts[0].xlatecuePartText = value.translation;
        cueParts[0].xlatecueTokenCount = numTokens;
        cueParts[0].xlatecueWordCount = numWords;
        cueParts[0].xlatecueCharCount = numChars;

    }


}

